import VEXLib.Units.Units as Units

try:
    import utime


    def time():
        """
        Get the current time
        Returns: The elapsed time since robot startup in seconds
        """
        return Units.nanoseconds_to_seconds(utime.ticks_us())
except ImportError:
    import time as utime


    def time():
        """
        Get the current time
        Returns: The elapsed time since robot startup in seconds
        """
        return utime.time()


def sleep(time_seconds):
    utime.sleep(time_seconds)
